import React, { useState, useEffect, Component } from 'react';

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";



export default function Period ({match}) {

    const token = localStorage.getItem('token')
    let periodstart = 0;
    let periodend = 0;


    let url = `http://localhost:8888/web/jsonapi/node/Periode?filter[janis-filter][condition][value]=${match.params.id}&filter[janis-filter][condition][path]=id`
    let urltasks = `http://localhost:8888/web/jsonapi/node/taak?filter[datum][condition][path]=field_datum&filter[datum][condition][operator]=BETWEEN&filter[datum][condition][value][]=${periodstart}&filter[datum][condition][value][]=${periodend}`

    fetch(url, {
        method: "GET",
        headers: {
            'Content-Type': 'application/vnd.api+json',
            'Accept': 'application/x-www-form-urlencoded',
            'Authorization': 'Bearer ' +  token
        },
    }).then((response)=> {
        return response.json()
    }).then((data)=> {
        let dates = data.data[0]
        console.log(dates.attributes)
        periodstart = dates.attributes.field_eind_periode;
        periodend = dates.attributes.field_start_periode;
    })

    fetch(urltasks, {
        method: "GET",
        headers: {
            'Content-Type': 'application/vnd.api+json',
            'Accept': 'application/x-www-form-urlencoded',
            'Authorization': 'Bearer ' +  token
        },
    }).then((response)=> {
        return response.json()
    }).then((data)=> {
        console.log(data.data[0].attributes.field_eind_periode);
        console.log(data.data[0].attributes.field_start_periode);
    })
  return(
    <h3>ID: {match.params.id}</h3>
  )
}
