import React, { useState, useEffect, Component } from 'react';

import ReactDOM from 'react-dom';


import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import ReactPDF from '@react-pdf/renderer';
import Mypdf from './Pdf'

export default function Period ({match}) {

    const token = localStorage.getItem('token')
    const [periodstart, setperiodstart] = useState("")
    const [periodend, setperiodend] = useState("")
    const [title, settitle] = useState("")
    const [taskdata, settaskdata] = useState("")



    let url = `http://localhost:8888/web/jsonapi/node/Periode?filter[janis-filter][condition][value]=${match.params.id}&filter[janis-filter][condition][path]=id`

    function getPeriodData(){


        fetch(url, {
            method: "GET",
            headers: {
                'Content-Type': 'application/vnd.api+json',
                'Accept': 'application/x-www-form-urlencoded',
                'Authorization': 'Bearer ' +  token
            },
        }).then((response)=> {
            return response.json()
        }).then((data)=> {
            let dates = data.data[0]
            settitle(dates.attributes.title)
            setperiodstart(dates.attributes.field_eind_periode);
            setperiodend(dates.attributes.field_start_periode);


        })
    }


    function getTasksData(){
    fetch(`http://localhost:8888/web/jsonapi/node/taak?filter[datum][condition][path]=field_datum&filter[datum][condition][operator]=BETWEEN&filter[datum][condition][value][]=${periodstart}&filter[datum][condition][value][]=${periodend}`, {
            method: "GET",
            headers: {
                'Content-Type': 'application/vnd.api+json',
                'Accept': 'application/x-www-form-urlencoded',
                'Authorization': 'Bearer ' +  token
            },
        }).then((response)=> {
            return response.json()
        }).then((data)=> {
            for(let i = 0; i<data.data.length; i++){
                console.log(data.data[i])
                // document.getElementById('tasks').innerHTML += `<div><h3>${data.data[i].attributes.title}</h3></div>`
            }
        })
    }

    useEffect(getPeriodData,[])
    getTasksData();

    var doc = new jsPDF({
        orientation: 'landscape',
        unit: 'in',
        format: [4, 2]
      })

      doc.text('Hello world!', 1, 1)
      doc.save('two-by-four.pdf')

       return (
          <div >

          </div>
        );
}

ReactPDF.render(<Mypdf />, `${__dirname}/example.pdf`);
