        'Accept': 'application/x-www-form-urlencoded',
        'Authorization': 'Bearer ' +  token,
        'X-CSRF-Token': 'KTpytkazFcgdgLqK33HpYx4YFb3w3a5olURFx2uAEXs'
      },
      body: JSON.stringify({
              "data": {
                "type": "node--taak",
                "attributes": {
                  "title": "foutmelding",
                  "field_aanvraag": message,
                },
              }
            })
  }).then((response) => {
    return response.json();
  }).then((data)=> {
    console.log(data)
  })
}
  return(
      <div class="bodyLogin">
      <h1>Foutmelding</h1>
      <input

                name="message"
                type="text"
                onChange={(e) => setmessage(e.target.value)}
            />
        <button onClick={handleSubmit}>Bekijk</button>

      </div>
  )
}
